extension MockPlayerNetwork:playersProtocol{
    static func fetchResult(complitionHandler: @escaping (Goallll.PlayerRoot?) -> Void, Update: String) {
        let data = Data(playerResponse.utf8)
        
        do{
            let res = try JSONDecoder().decode(PlayerRoot.self, from: data)
            complitionHandler(res)
        }catch let error{
            print(error.localizedDescription)
            complitionHandler(nil)
        }
    }
